sys_variable_value.value:
(function execute(inputs, outputs) {
	var gr = new GlideRecord('sc_req_item');
      gr.get(inputs.ritm.sys_id);
      gr.query()

      gr.variables["pmo"] = inputs.pmo.sys_id;
      gr.update();
})(inputs, outputs);

