sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var requested_for_name = current.variables["requested_for"].name.toString();
	var point_of_contact_name = current.variables["point_of_contact"].name.toString();

	var itpm_name = current.variables["itpm"] ? current.variables["itpm"].name.toString() :'';
	var pmo_name = current.variables["pmo"] ? current.variables["pmo"].name.toString() : '';
	var purpose = current.variables["purpose"].getDisplayValue();
	var desired_implementation_date = current.variables["desired_implementation_date"];
	var program = current.variables["program"].getDisplayValue();
	var location = current.variables["location"].getDisplayValue();
	var short_description = current.variables["short_description"];
	var description = current.variables["description"];


	template.print("<b>Requested for: </b>" + requested_for_name + "<br />");
	template.print("<b>Point of Contact: </b>" + point_of_contact_name + "<br />");
	if (itpm_name != '') template.print("<b>ITPM: </b>" + itpm_name + "<br />");
	if (pmo_name != '') template.print("<b>PMO: </b>" + pmo_name + "<br />");
	template.print("<b>Purpose: </b>" + purpose + "<br />");
	template.print("<b>Desired Implementation Date: </b>" + desired_implementation_date + "<br />");
	template.print("<b>Program: </b>" + program + "<br />");
	template.print("<b>Location: </b>" + location + "<br /><hr>");
	template.print("<b>Short Description: </b>" + short_description + "<br />");
	template.print("<b>Description: </b>" + description + "<br />");

})(current, template, email, email_action, event);
