sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
	var r4 = current.variables["requested_for"].email.toString();
	var r4_name = current.variables["requested_for"].name.toString();	
	var poc = current.variables["point_of_contact"].email.toString();
	var poc_name = current.variables["point_of_contact"].name.toString();

	email.addAddress('cc', r4, r4_name);
	email.addAddress('cc', poc, poc_name);


})(current, template, email, email_action, event);
